import React from 'react';
import Image from '../image/index';
import './index.css'

function Detail(props) {
  const {
    now = {},
    setPage,
  } = props;

  const weather = now.text?.includes('雨') ? 'rain' : 
    now.text?.includes('雪') ? 'snow' : 
    now.text?.includes('晴') ? 'sun' :
    now.text?.includes('云') ? 'cloud' : 'overcast';

  return (
    <div>
      <img className="detail-back" src={Image.back} onClick={() => setPage(1)} />
      <img className="detail-weather" src={Image[weather]} />
      <p className="detail-position">
        <span>浙江省,</span>
        <span>杭州市</span>
      </p>
      <div className="detail-temp">
        {now.temp || '*'}
        <span className="detail-icon">°c</span>
      </div>
    </div>
  );
}

export default Detail;
