import weatherLogo from './image';
import './App.css';
import { useEffect, useState } from 'react';

// sun,
// rain,
// snow,
// cloud,
// overcast,

const dayMap = ['周日','周一','周二','周三','周四','周五','周六',];

function App() {
  const [now, setNow] = useState({});
  const [page, setPage] = useState(1);

  useEffect(async () => {
    requestWeather();
    console.log(weatherLogo)
  }, []);

  const requestWeather = async () => {

    const result = await fetch('https://devapi.qweather.com/v7/weather/now?location=101210106&key=d822b681e139418ea3e02807da626ada').then(data => data.json());
    // https://devapi.qweather.com/v7/weather/3d?location=101210106&key=d822b681e139418ea3e02807da626ada
    console.log(result);
    if (result.code === '200') {
      setNow(result.now);
      // // 免费版只有3天日期
      // let weatherList = result.daily;
      // weatherList = weatherList.concat(weatherList, weatherList[0]).map((item, index) => {
      //   const time = +new Date() + 86400000 * index;
      //   const fxDate = time.getFullYear()
      //   return Object.assign({}, item, {
      //     fxDate: 
      //   });
      // });
    } else {
      throw new Error('request error');
    }
  };
  const nowTime = new Date(now.obsTime);
  const timeText = dayMap[nowTime.getDay()] + ' ' + nowTime.getHours() + (nowTime.getHours() > 12 ? 'pm' : 'am');
  debugger
  return (
    <div className="App">
      <div className="content">
        <img className="weatherLogo" src={weatherLogo['sun']} />
        <div className="position">杭州市, 浙江省</div>
        <div className="description">
          <div className="wrapper">
            <span className="temperature">15<span className="icon">°c</span></span>
            <span className="date">{timeText}</span>
          </div>
          <div className="wrapper">
            <span className="desc1">强风</span>
            <span className="desc1 desc2">多云</span>
          </div>
        </div>
        <div className="detail">详情</div>
      </div>
      <div>
        <ul className="tips">
          <li>
            <span>
              <img className="icon" src={weatherLogo.rainIcon} />降水
            </span>
            <span className="tip">7%</span>
          </li>
          <li>
            <span>
              <img className="icon temIcon" src={weatherLogo.temIcon} />湿度
            </span>
            <span className="tip">80%</span>
          </li>
          <li>
            <span>
              <img className="icon" src={weatherLogo.windIcon} />风速
            </span>
            <span className="tip">19km/h</span>
          </li>
        </ul>
      </div>
    </div>
  );
}

export default App;
