import React from 'react';
import Image from '../image/index';
import './index.css'

function Detail(props) {
  const {
    now,
    setPage,
  } = props;

  const weather = now.text?.includes('雨') ? 'rain' : 
    now.text?.includes('雪') ? 'snow' : 
    now.text?.includes('晴') ? 'sun' :
    now.text?.includes('云') ? 'cloud' : 'overcast';

  return (
    <div>
      <img className="back" src={Image.back} onClick={() => setPage(1)} />
      <img className="weatherIcon" src={Image[weather]} />
    </div>
  );
}

export default Detail;
